#!/bin/sh
#------------------------------------------------------------------------------
# mkmetadatares.sh
# Package an XML metadata file in to a Windows resource script.
#------------------------------------------------------------------------------

outfile=$1
metadata=$2

cat > ${outfile} << EOF
// This file is automatically generated.

17485 RCDATA 
{
EOF

cat ${metadata} | sed "s:\":\"\":g" | \
  sed "s:\(.*\):  \"\1\",:g" >> ${outfile}

cat >> ${outfile} << EOF
  "\0"
}

EOF
