/** Immediately switch to a different input stream.
 * @param input_file A readable stream.
 * 
 * @note This function does not reset the start condition to @c INITIAL .
 */
    void awsrestart  (FILE * input_file )
{
    
	if ( ! YY_CURRENT_BUFFER ){
        awsensure_buffer_stack ();
		YY_CURRENT_BUFFER_LVALUE =
            aws_create_buffer(awsin,YY_BUF_SIZE );
	}

	aws_init_buffer(YY_CURRENT_BUFFER,input_file );
	aws_load_buffer_state( );
}

/** Switch to a different input buffer.
 * @param new_buffer The new input buffer.
 * 
 */
    void aws_switch_to_buffer  (YY_BUFFER_STATE  new_buffer )
{
    
	/* TODO. We should be able to replace this entire function body
	 * with
	 *		awspop_buffer_state();
	 *		awspush_buffer_state(new_buffer);
     */
	awsensure_buffer_stack ();
	if ( YY_CURRENT_BUFFER == new_buffer )
		return;

	if ( YY_CURRENT_BUFFER )
		{
		/* Flush out information for old buffer. */
		*(yy_c_buf_p) = (yy_hold_char);
		YY_CURRENT_BUFFER_LVALUE->yy_buf_pos = (yy_c_buf_p);
		YY_CURRENT_BUFFER_LVALUE->yy_n_chars = (yy_n_chars);
		}

	YY_CURRENT_BUFFER_LVALUE = new_buffer;
	aws_load_buffer_state( );

	/* We don't actually know whether we did this switch during
	 * EOF (awswrap()) processing, but the only time this flag
	 * is looked at is after awswrap() is called, so it's safe
	 * to go ahead and always set it.
	 */
	(yy_did_buffer_switch_on_eof) = 1;
}

