static int yy_init_globals (void)
{
        /* Initialization is the same as for the non-reentrant scanner.
     * This function is called from awslex_destroy(), so don't allocate here.
     */

    /* We do not touch awslineno unless the option is enabled. */
    awslineno =  1;
    
    (yy_buffer_stack) = 0;
    (yy_buffer_stack_top) = 0;
    (yy_buffer_stack_max) = 0;
    (yy_c_buf_p) = (char *) 0;
    (yy_init) = 0;
    (yy_start) = 0;

/* Defined in main.c */
#ifdef YY_STDINIT
    awsin = stdin;
    awsout = stdout;
#else
    awsin = (FILE *) 0;
    awsout = (FILE *) 0;
#endif

    /* For future reference: Set errno on error, since we are called by
     * awslex_init()
     */
    return 0;
}

/* awslex_destroy is for both reentrant and non-reentrant scanners. */
int awslex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		aws_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		awspop_buffer_state();
	}

	/* Destroy the stack itself. */
	awsfree((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    /* Reset the globals. This is important in a non-reentrant scanner so the next time
     * awslex() is called, initialization will occur. */
    yy_init_globals( );

    return 0;
}

